package com.dkte;

public class maxnum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a =5,b=6,c=8;
		if(a>b && a>c)
		{
			System.out.println("a is max number");
		}
		else if(b>a && b>c)
		{
			System.out.println("b is max number");
		}
		else
		{
			System.out.println("c is max number");
		}

	}

}
